mapper.delete:
DELETE FROM winning_record
WHERE activity_id = #{activityId}

    AND prize_id = #{prizeId}

AND winner_id IN

    #{winnerId}

DELETE FROM winning_record
WHERE activity_id = #{activityId}

    AND prize_id = #{prizeId}

mapper.select:
SELECT *
FROM winning_record
WHERE activity_id = #{activityId}

    AND prize_id = #{prizeId}

SELECT COUNT(1)
FROM winning_record
WHERE activity_id = #{activityId}
  AND prize_id = #{prizeId}
